import Foundation

func findTheDifference(_ s: String, _ t: String) -> Character {


    var sDict = [Character: Int]()
    var tDict = [Character: Int]()
    
    for c in s {
        if sDict.keys.contains(c) {
            sDict[c]! += 1
        } else {
            sDict[c] = 1
        }
    }
    for c in t {
        if tDict.keys.contains(c) {
            tDict[c]! += 1
        } else {
            tDict[c] = 1
        }
    }
    for d in tDict {
        if sDict.keys.contains(d.key) {
            if sDict[d.key] != d.value {
                return d.key
            }
        } else {
            return d.key
        }
    }
    return "0"
}
findTheDifference("", "")
